mapper.select:
SELECT

FROM demo_account WHERE 1=1

	AND name_ = #{name_}

		SELECT
			count(id_) as count_,
			IFNULL(sum(balance_),0) as sum_balance_,
			IFNULL(sum(credit_line_),0) as sum_credit_line_,
			IFNULL(max(age_),0) as max_age_
		FROM
		demo_account
		
			
				AND name_ = #{name_}
			
			
				AND card_type_ = #{card_type_}
			
			
				AND card_id_ = #{card_id_}
